// --- 自動處理程式碼 (合併 + 排序 + 分組) ---
const rawWordData = part1.concat(part2, part3);

const masterWordList = (function() {
    const sortedList = rawWordData.sort((a, b) => 
        a.word.toLowerCase().localeCompare(b.word.toLowerCase())
    );

    const letterCounts = {};
    sortedList.forEach(item => {
        const letter = item.word.charAt(0).toUpperCase();
        letterCounts[letter] = (letterCounts[letter] || 0) + 1;
    });

    const letterProgress = {}; 

    return sortedList.map(item => {
        const letter = item.word.charAt(0).toUpperCase();

        if (!letterProgress[letter]) letterProgress[letter] = 0;
        letterProgress[letter]++;

        let sessionId;
        if (letterCounts[letter] <= 50) {
            sessionId = letter; 
        } else {
            const subIndex = Math.ceil(letterProgress[letter] / 50);
            sessionId = `${letter}-${subIndex}`;
        }

        return {
            session: sessionId,
            word: item.word,
            sentence: item.sentence,
            word_zh: item.word_zh,
            sentence_zh: item.sentence_zh
        };
    });
})();